import React from 'react';
import './directory.styles.scss';

import { connect } from 'react-redux'

import MenuItem from '../menu-item/menu-item.component';

class Directory extends React.Component {
    constructor() {
        super();

        this.state = {
            sections: [
                {
                    title: 'hats',
                    imageUrl: 'https://i.ibb.co/cvpntL1/hats.png',
                    id: 1,
                    linkUrl: 'shop/hats'
                },
                {
                    title: 'jackets',
                    imageUrl: 'https://i.ibb.co/px2tCc3/jackets.png',
                    id: 2,
                    linkUrl: 'shop/jackets'
                },
                {
                    title: 'sneakers',
                    imageUrl: 'https://i.ibb.co/0jqHpnp/sneakers.png',
                    id: 3,
                    linkUrl: 'shop/sneakers'
                },
                {
                    title: 'womens',
                    imageUrl: 'https://i.ibb.co/GCCdy8t/womens.png',
                    size: 'large',
                    id: 4,
                    linkUrl: 'shop/womens'
                },
                {
                    title: 'mens',
                    imageUrl: 'https://i.ibb.co/R70vBrQ/men.png',
                    size: 'large',
                    id: 5,
                    linkUrl: 'shop/mens'
                }
            ]

        }

    }
    render() {
        return (
            <div className="directory-menu">
                {this.state.sections.map(({ id, ...otherSectionPorps }) => {
                    return <MenuItem
                        key={id}
                        {...otherSectionPorps} />
                })}
            </div>

        );
    }
}

const mapStateToProps = (state) => ({
    directory_data: state.DIRECTORY_DATA
})

export default connect(mapStateToProps)(Directory);
//ES6 allow us to pass props like {id,...otherSectionProps} ,when we pass the props
// it's name=name .like title=title,imageUrl=imageUrl ... ={...otherSectionPorps}
