import React from 'react';
import { Route } from 'react-router-dom';

import { firestore, convertCollectionsSnapshotToMap } from '../../firebase/firebase.utils';


import CollectionOverview from '../../components/collection-overview/collection-overview.component';
import CollectionPage from '../collection/collection.component';


class ShopPage extends React.Component {

    unsubscribeFromShop = null;

    componentDidMount() {
        const collectionRef = firestore.collection('itemCollections')
        this.unsubscribeFromShop = collectionRef.onSnapshot(async snapshot => convertCollectionsSnapshotToMap(snapshot))
    }

    componentWillUnmount() {
        this.unsubscribeFromShop()
    }
    render() {
        return (
            <div className='shop-page'>
                <Route exact path={`${this.props.match.path}`} component={CollectionOverview} />
                <Route path={`${this.props.match.path}/:collectionId`} component={CollectionPage} />
            </div>
        );
    }
}


export default ShopPage;
