export const addItemToCart = (cartItems, item) => {
    if (cartItems.length > 0) {
        cartItems.forEach(cartItem => {
            if (cartItem.id === item.id) {
                cartItem.qt++
            } else {
                cartItems.push(item)
            }
        })
    } else {
        cartItems.push(item)
    }
}
