export const addItemToCart = (cartItems, item) => {
    let updateCartItems = [...cartItems]
    if (updateCartItems.length > 0) {
        updateCartItems.forEach(cartItem => {
            if (cartItem.id === item.id) {
                cartItem.qt++
            } else {
                updateCartItems.push(item)
            }
        })
    } else {
        updateCartItems.push(item)
    }

    return updateCartItems
}
